import "../App.css";

const StageOne = ({ stageTwo, handlePoints, points }) => {
  const alerta = document.querySelector(".alert");
  const alerta2 = document.querySelector(".alert2");

  let ponto = points;
  
  const verifyPoints = () => {
    stageTwo();
    handlePoints();
  }

  const verifyAlert = () => {
    alert.style.display = "none";
    stageTwo();
  }

  return (
    <div>
      <h1>Yellow Star</h1>
      <p>Pontuação: <span>{ponto}</span> </p>
      <div>
        <button className="car car1" onClick={stageTwo}></button>
        <button className="car car2" onClick={stageTwo}></button>
      </div>
      <div className="alert">
        Acertou!! &nbsp; <span className="closed" onClick={verifyAlert} > &times;  </span>
      </div>
      <div className="alert2">
        Errou!! &nbsp; <span className="closed"> &times; </span>
      </div>
      <div>
        <button className="car car3" onClick={verifyPoints}></button>
        <button className="car car4" onClick={stageTwo} ></button>
      </div>
    </div>
  )
}

export default StageOne;
