//CSS
import './App.css';

//Componentes


import { useState, useEffect } from 'react';
import Comecar from './components/Comecar';
import Stage_one from './components/StageOne';

const stages = [
  {id: 1, name: "start"},
  {id: 2, name: "gameOne"},
  {id: 3, name: "gameTwo"},
  {id: 4, name: "gameThree"},
  {id: 5, name: "gameFour"},
  {id: 6, name: "end"},
];

function App() {

  // Começando o game
  const [gameStage, setGameStage] = useState(stages[0].name);

  const comecar = () => {
    setGameStage(stages[2].name)
  }

  return (
    <div className="App">
      {gameStage === "start" && <Comecar comecar={comecar}/>}
      {gameStage ==="gameOne" && <Stage_one />}
    </div>
  );
}

export default App;
