import "../App.css";

const StageOne = ({ stageTwo, handlePoints, points }) => {
  const alerta = document.querySelector(".alert");
  const alerta2 = document.querySelector(".alert2");

  let ponto = points;
  
  const verifyPoints = () => {
    stageTwo();
    handlePoints();
  }

  const closeAlert = () => {
    alerta.style.display = "none";
    verifyPoints();
  }

  const closeAlert2 = () => {
    alerta2.style.display = "none";
    stageTwo();
  }

  const openAlert = () => {
    alerta.style.display = "block";
  }

  const openAlert2 = () => {
     alerta2.style.display = "block";
  }

  return (
    <div>
      <h1>Yellow Star</h1>
      <p>Pontuação: <span>{ponto}</span> </p>
      <div>
        <button className="car car1" onClick={openAlert2}></button>
        <button className="car car2" onClick={openAlert2}></button>
      </div>
      <div className="alert">
        Acertou!! &nbsp; <span className="closed" onClick={closeAlert} > &times;  </span>
      </div>
      <div className="alert2">
        Errou!! &nbsp; <span className="closed" onClick={closeAlert2} > &times; </span>
      </div>
      <div>
        <button className="car car3" onClick={openAlert}></button>
        <button className="car car4" onClick={openAlert2} ></button>
      </div>
    </div>
  )
}

export default StageOne;
