//CSS
import './App.css';

//Componentes


import { useState, useEffect } from 'react';
import Comecar from './components/Comecar';

const stages = [
  {id: 1, name: "start"},
  {id: 2, name: "gameOne"},
  {id: 3, name: "gameTwo"},
  {id: 4, name: "gameThree"},
  {id: 5, name: "gameFour"},
  {id: 6, name: "end"},
];

function App() {

  // Começando o game
  const [gameStage, setGameStage] = useState(stages[0].name);

  return (
    <div className="App">
      {gameStage === "start" && <Comecar stage={"start"}/>}
    </div>
  );
}

export default App;
